import React, { useContext, useState } from "react";
import "./ProductDisplay.css";
import star_icon from "../Assets/star_icon.png";
import star_dull_icon from "../Assets/star_dull_icon.png";
import { ShopContext } from "../../Context/ShopContext";

const ProductDisplay = (props) => {
  const { product } = props;
  const { addToCart } = useContext(ShopContext);

  const [selectedSize, setSelectedSize] = useState(null);

  return (
    <div className="productdisplay">
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          <img src={product.image} alt="" />
          <img src={product.image} alt="" />
          <img src={product.image} alt="" />
          <img src={product.image} alt="" />
        </div>
        <div className="productdisplay-img">
          <img className="productdisplay-main-img" src={product.image} alt="" />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.name}</h1>
        <div className="productdisplay-right-stars">
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_dull_icon} alt="" />
          <p>(4)</p>
        </div>
        <div className="productdisplay-right-prices">
          <div className="productdisplay-right-prices-old">
            ${product.old_price}
          </div>
          <div className="productdisplay-right-prices-new">
            ${product.new_price}
          </div>
        </div>
        <div className="productdisplay-right-description">
          A lightweight,usually knitted, pullover shirt, close-fitting, with a
          round neckline and short sleeves, worn as an undershirt or outershirt
          garment.
        </div>
        <div className="productdisplay-right-size">
          <h1>Select Size</h1>
          <div className="productdisplay-right-sizes">
            <button
              onClick={() => handleSizeSelection("S")}
              className={product.selectedSize === "S" ? "selected" : ""}
            >
              S
            </button>
            <button
              onClick={() => handleSizeSelection("M")}
              className={product.selectedSize === "M" ? "selected" : ""}
            >
              M
            </button>
            <button
              onClick={() => handleSizeSelection("L")}
              className={product.selectedSize === "L" ? "selected" : ""}
            >
              L
            </button>
            <button
              onClick={() => handleSizeSelection("XL")}
              className={product.selectedSize === "XL" ? "selected" : ""}
            >
              XL
            </button>
            <button
              onClick={() => handleSizeSelection("XXL")}
              className={product.selectedSize === "XXL" ? "selected" : ""}
            >
              XXL
            </button>
          </div>
        </div>
        <button
          onClick={() => {
            addToCart(product.id);
          }}
        >
          ADD TO CART
        </button>
        {/* <p className="productdisplay-right-category">
          <span>Category :</span>Women, T-Shirt, Crop Top
        </p>
        <p className="productdisplay-right-category">
          <span>Tag :</span>Modern, Latest
        </p> */}
      </div>
    </div>
  );
};

export default ProductDisplay;
